mapper.update:
UPDATE PROJECT_STUDY
PROJECT_STUDY_TYPE = #{projectStudyType}, PROJECT_STUDY_ONOFFLINE = #{projectStudyonoffline}, PROJECT_STUDY_PERSON_NUM = #{projectStudyPersonNum}, PROJECT_STUDY_START_DATE = #{projectStudyStartDate}, PROJECT_STUDY_CONTACT = #{projectStudyContact}, PROJECT_STUDY_PERIOD = #{projectStudyPeriod}, PROJECT_STUDY_LANGUAGE = #{projectStudyLanguage}, PROJECT_STUDY_HIT = #{projectStudyHit}, PROJECT_STUDY_TITLE = #{projectStudyTitle}, PROJECT_STUDY_SUBJECT = #{projectStudySubject}
WHERE PROJECT_STUDY_ID = #{projectStudyId}

mapper.select:
SELECT * FROM PROJECT_STUDY


		PROJECT_STUDY_TYPE = #{projectStudyType}


		AND PROJECT_STUDY_LANGUAGE = #{projectStudyLanguage}



ORDER BY PROJECT_STUDY_ID DESC
